insert into `local_in-jure`.news
    (id, title, header, long_read)
values (uuid(),
        'Какие ошибки отметила антимонопольная служба при применении Закона № 44-ФЗ в августе 2024 года.',
        'Заказчики неверно оценивали опыт участников, незаконно отклоняли заявки при закупках с дополнительными требованиями, а также некорректно формулировали типовое условие о привлечении СМП и СОНКО на субподряд.',
        '<div><p>Заказчики неверно оценивали опыт участников, незаконно отклоняли заявки при закупках с дополнительными требованиями, а также некорректно формулировали типовое условие о привлечении СМП и СОНКО на субподряд.</p><h2>Неверный порядок оценки</h2><p>При оценке квалификации госзаказчик применял показатель «Общая цена исполненных договоров», при этом предельное минимальное значение характеристики составило 111% НМЦК. ФАС признала порядок оценки незаконным. Участников с опытом исполнения контрактов, сопоставимых с НМЦК, лишили права получить баллы по показателю, что противоречит принципам контрактной системы, поскольку не позволяет выявить лучшее предложение. Суды отметили, что сопоставимым был опыт исполнения одного контракта с ценой, равной НМЦК.</p><h2>Незаконное отклонение заявки при закупке с дополнительными требованиями</h2><p>Заказчик проводил закупку работ по ремонту дороги и установил дополнительные требования. Заявку одного из участников отклонили, так как он представил для подтверждения опыта контракт без приложений и реестровую запись. ФАС решила, что отстранять участника не следовало, поскольку Постановление № 2571 разрешает подтверждать опыт реестровой записью, а в реестре контрактов была вся нужная заказчику информация.</p><h2>Некорректное условие о привлечении СМП и СОНКО на субподряд</h2><p>Проект контракта содержал условие о привлечении СМП и СОНКО на субподряд в объеме не менее 28% цены контракта. ФАС сочла формулировку некорректной, в связи с несоответствием типовым условиям, поскольку объем привлечения устанавливают в виде фиксированного процента.</p></div>'),
       (uuid(),
        'ФАС озвучила планы по решению проблем, связанных с госзакупками, в том числе недостоверными сведениями в заявках участников закупки',
        'ФАС нередко выявляет случаи, когда побеждают участники с подложными документами, антимонопольный орган предлагает включать сведения в РНП об участниках, предоставивших недостоверные сведения в заявках.',
        '<p>На пленарной сессии III Всероссийской конференции в сфере закупок ФАС перечислила проблемы отрасли и сообщила о планируемых решениях.</p><p>ФАС нередко выявляет случаи, когда побеждают участники с подложными документами, антимонопольный орган предлагает включать сведения в РНП об участниках, предоставивших недостоверные сведения в заявках.</p><p>В целях снижения случаев несогласования ФАС увеличения цены строительных контрактов при удорожании материалов для определения объективных рыночных цен на стройресурсы будут применяться биржевые индикаторы.</p><p>Также антимонопольный орган работает над внедрением рейтинга деловой репутации, в который будут попадать данные о том, как участники исполняют требования налогового, трудового и закупочного законодательства. Предполагается, что лица с более высокими показателями смогут претендовать на снижение размеров обеспечения и банковских комиссий за выдачу гарантий.</p>'),
       (uuid(),
        'С 22 октября 2024 года банки будут обязаны соблюдать новые правила в целях борьбы с переводами денег мошенникам',
        'Системно значимые кредитные организации, а также кредитные организации из реестра значимых на рынке платежных услуг будут применять информацию из базы данных о переводах средств без согласия клиентов не позже 2 часов с момента, когда ЦБ РФ ее предоставит. ',
        '<p>Системно значимые кредитные организации, а также кредитные организации из реестра значимых на рынке платежных услуг будут применять информацию из базы данных о переводах средств без согласия клиентов не позже 2 часов с момента, когда ЦБ РФ ее предоставит. Остальные операторы по переводу денег с 22 октября 2024 года будут выполнять обязанность в течение 3 часов с того же момента без дальнейшего сокращения срока.</p><p>Если сведения поступят, когда операторы не обслуживают клиентов (в т.ч. через интернет-банкинг), исчисление сроков начнется с момента запуска такого сопровождения.</p><p>ЦБ РФ отметил, что нововведения снизят число денежных переводов мошенникам.</p><p>Сейчас время, в течение которого банки и другие операторы должны применять информацию для выявления переводов денег без согласия клиентов, не установлено.</p>'),
       (uuid(),
        'Верховный Суд Российской Федерации уточнил, когда заявитель вправе возместить государственную пошлину',
        'Экономколлегия ВС РФ решала, может ли компания претендовать на возмещение судебных расходов, в случае если апелляция не отменила решение, а лишь изменила время его исполнения.',
        '<p>Системно значимые кредитные организации, а также кредитные организации из реестра значимых на рынке платежных услуг будут применять информацию из базы данных о переводах средств без согласия клиентов не позже 2 часов с момента, когда ЦБ РФ ее предоставит. Остальные операторы по переводу денег с 22 октября 2024 года будут выполнять обязанность в течение 3 часов с того же момента без дальнейшего сокращения срока.</p><p>Если сведения поступят, когда операторы не обслуживают клиентов (в т.ч. через интернет-банкинг), исчисление сроков начнется с момента запуска такого сопровождения.</p><p>ЦБ РФ отметил, что нововведения снизят число денежных переводов мошенникам.</p><p>Сейчас время, в течение которого банки и другие операторы должны применять информацию для выявления переводов денег без согласия клиентов, не установлено.</p>'),
       (uuid(),
        'Суды не поддержали работодателя: сотрудника уволили в пятницу, однако должность сократили с понедельника',
        'Из-за сокращения с работником расторгли трудовой договор, но должность оставалась в штатном расписании еще 2 дня. Ее исключили только после выходных. Специалист оспорил увольнение.',
        '<p>Из-за сокращения с работником расторгли трудовой договор, но должность оставалась в штатном расписании еще 2 дня. Ее исключили только после выходных. Специалист оспорил увольнение.</p><p>Суды среди прочего отметили: порядок сокращения нарушен, так как в последний рабочий день сотрудника должность не убрали из штатного расписания. Увольнение, как правило, можно оформить не ранее той даты, когда исключают ставку. Кассация отклонила довод работодателя о том, что должность сократили после 2 выходных.</p><p>Ранее суды, например 6-й КСОЮ, также обращали внимание на то, есть ли ставка в штатном расписании на момент увольнения.</p>'),
       (uuid(),
        'Конституционный Суд Российской Федерации отметил: отсутствие в протоколе о правонарушении подписи составителя не повод для суда прекратить дело',
        'С 4 октября 2024 года, если судья при рассмотрении дела по существу выявляет, что в протоколе об АП нет подписи составителя, его вызывают в заседание',
        '<p>С 4 октября 2024 года, если судья при рассмотрении дела по существу выявляет, что в протоколе об АП нет подписи составителя, его вызывают в заседание. Цель – подтвердить, что именно это должностное лицо оформило документ, и продолжить разбирательство. Правило действует, пока в КоАП РФ не определят, как судье устранять такой недостаток протокола.</p><p>Ранее в данной ситуации судья прекращал производство. Кроме того, Верховный Суд Российской Федерации признавал протокол без подписи составителя недопустимым доказательством.</p>'),
       (uuid(),
        'Суды напомнили: замена некачественного товара по госконтракту исключает возможность взыскания штрафа с поставщика',
        'При исполнении контракта на поставку мазута заказчик выявил несколько партий некачественного товара. Поставщику начислили штраф, но тот его не уплатил. Стороны встретились в суде.',
        '<p>При исполнении контракта на поставку мазута заказчик выявил несколько партий некачественного товара. Поставщику начислили штраф, но тот его не уплатил. Стороны встретились в суде.</p><p>Апелляция и кассация не увидели оснований для штрафа, поскольку некачественная продукция была заменена. По мнению судов, факта замены недостаточно для взыскания штрафа, ведь в итоге заказчик получил надлежащий товар и его интерес был удовлетворен.</p><p>Вывод основан на позиции Верховного Суда Российской Федерации.</p>'),
       (uuid(),
        'Верховный Суд Российской Федерации решил: выходные и праздники не относятся к уважительным причинам пропуска срока на обжалование',
        'В октябре 2022 года суд составил мотивированное решение, которое ответчик получил лишь 14 декабря. Он направил в суд апелляционную жалобу 27 января 2023 года и просил восстановить срок на ее подачу.',
        '<p>В октябре 2022 года суд составил мотивированное решение, которое ответчик получил лишь 14 декабря. Он направил в суд апелляционную жалобу 27 января 2023 года и просил восстановить срок на ее подачу.</p><p>Первая инстанция отказала, так как прошло больше месяца с даты, когда ответчик получил копию судебного акта.</p><p>Апелляция не согласилась и отметила: с 31 декабря 2022 года по 8 января 2023 года были праздничные и выходные дни, поэтому ответчик пропустил срок по уважительным причинам. Кассация вывод поддержала.</p><p>ВС РФ оставил в силе отказ первой инстанции:</p><ul><li>если в середине срока на обжалование есть выходные и праздничные дни, которые законом установили заранее, это само по себе не доказывает, что срок пропустили по уважительной причине;</li><li>просрочка составила даже большее число дней, чем «новогодние каникулы»;</li><li>в заявлении о восстановлении срока ответчик не объяснил причину пропуска с даты, когда получил мотивированное решение;</li><li>в частной жалобе ответчик сослался на болезнь (временное отсутствие) юридического консультанта, о чем не заявлял в первой инстанции. Более того, эта причина не является уважительной, как разъяснял Пленум Верховного Суда Российской Федерации.</li></ul>'),
       (uuid(),
        'Верховный Суд Российской Федерации решил: выходные и праздники не относятся к уважительным причинам пропуска срока на обжалование',
        'Банки не смогут получать от застройщиков плату за понижение ставки по ипотеке, если это ведет к удорожанию недвижимости. Ипотечные средства нельзя будет долго держать на аккредитиве. В первоначальный взнос запретят включать кредитные средства и «ипотечный кешбэк». ',
        '<p>Банки не смогут получать от застройщиков плату за понижение ставки по ипотеке, если это ведет к удорожанию недвижимости. Ипотечные средства нельзя будет долго держать на аккредитиве. В первоначальный взнос запретят включать кредитные средства и «ипотечный кешбэк». Новшества заработают с 1 января 2025 года и будут распространяться только на договоры, заключенные после этой даты.</p<ul><li><h4>Субсидирование процентной ставки</h4><p>Банк не сможет получать от продавца (застройщика) или иных подконтрольных ему лиц плату за понижение процентной ставки, если при этом увеличивается цена недвижимости. Исключение: застройщик или подконтрольное ему лицо – работодатель заемщика.</p><p>С 1 июля 2025 года запретят принимать сходное вознаграждение в том числе от заемщика. До этой даты будут действовать такие правила:</p><p>плата за снижение ставки вносится только за счет средств заемщика;</p><p>до заключения соглашения о снижении ставки заемщику предоставляют расчет полной стоимости кредита для обычной и пониженной ставки;</p><p>при досрочном погашении банк компенсирует заемщику переплату вознаграждения за неиспользованный период.</p></li><li><h4>Размещение ипотечных средств на аккредитиве</h4><p>Если банк размещает ипотечные средства на аккредитиве, условие о такой форме расчетов нужно включить в кредитный договор. При этом срок для зачисления денег на счет продавца (застройщика) или счет эскроу – не более 5 рабочих дней с момента выполнения условий для этого.</p></li><li><h4>Учет «ипотечного кешбэка» и кредитных средств в составе первого взноса</h4><p>Банки не смогут учитывать в составе первоначального взноса по ипотеке:</p><p>материальную выгоду от продавца (застройщика), банка или иных лиц при покупке предмета ипотеки;</p><p>кредиты, которые выданы банком наряду с ипотекой и потрачены заемщиком на приобретение той же недвижимости.</p><p>Условие запрета: при выдаче ипотеки банк знал о факте и сумме выгоды или использовании кредита из доступных ему договоров.</p></li><li><h4>Информирование о рисках</h4><p>Банк будет сообщать заемщику о рисках до заключения договора ипотеки, например информацию о возможной разнице между справедливой стоимостью недвижимости и ее ценой при покупке в ипотеку.</p></li</ul')
